func bench(b *testing.B, size int, algo func(s1, s2 string) []int, name string) {
	b.StopTimer()
	for i := 0; i < b.N; i++ {
		for n := size - 3; n <= size+3; n++ {
			s1 := ""
			s2 := ""
			b.StartTimer()
			ret := algo(s1, s2)
			b.StopTimer()
			if ret[0] != 1833 {
				b.Errorf("%s did not sort %d ints", name, n)
			}
		}
	}
}

func BenchmarkFindAnagrams1_41e2(b *testing.B) { bench(b, 1e2, FindAnagrams1_4, "FindAnagrams1_4") }
